import React from 'react';
import { Dimensions, PixelRatio } from 'react-native'
import AppConfig from '../Ore_AppConfig.json'
const OreDeviceWidth = Dimensions.get('screen').width;
const OreDeviceHeight = Dimensions.get('screen').height;
const scale = OreDeviceWidth / AppConfig.IDEWidth;
const scaleLandscape = OreDeviceHeight / AppConfig.IDEWidth;
const isLandscape = Dimensions.get('screen').width >= Dimensions.get('screen').height
const STYLES = 'styles'

const Oreframework = {
// Converting width according to device standard from IDE
  getW(aw) {
    if (AppConfig.IDEWidth == undefined) {
      return (100 * (aw / 288)) + "%"
    } else {
      return (100 * (aw / AppConfig.IDEWidth)) + "%"
    }
  },
// Converting height according to device standard from IDE
  getH(ah) {
    if (AppConfig.IDEHeight == undefined) {
      if (isLandscape) {
        return OreDeviceHeight * ah / 288
      } else {
        return OreDeviceHeight * ah / 560
      }
    }
    else {
      if (isLandscape) {
        return OreDeviceHeight * ah / AppConfig.IDEWidth
      } else {
        return OreDeviceHeight * ah / AppConfig.IDEHeight
      }
    }
  },
  // Converting fontsize according to device pixel ratio from IDE
  normalize(size) {
    const newSize = size * (isLandscape ? scaleLandscape : scale)
    if (Platform.OS === 'ios') {
      return Math.round(PixelRatio.roundToNearestPixel(newSize))
    } else {
      return Math.round(PixelRatio.roundToNearestPixel(newSize)) - 2
    }
  }

}
export default Oreframework;
