import {Alert} from 'react-native'

const OreAlert={
   // Default simple Alert function
   alert(message){
      alert('Alert', message, "OK",  () =>{console.log('Possitive Pressed')}, true);       
   },
   // Alert with title
   alert(title, message){
      alert(title, message, "OK",  () =>{console.log('Possitive Pressed')}, true);       
   },
   // Alert with title,message and confirmation button option
   alert(title, message, positiveText){
      alert(title, message, positiveText,  () =>{console.log('Possitive Pressed')}, true);       
   },
   // Alert with tilte , message , ok and cancel option
   alert(title, message, positiveText, cancelableFlag){
      alert(title, message,[{text: positiveText, onPress: () => console.log('Possitive Pressed')},], cancelableFlag);       
   },
   // Alert with button events
   alert(title, message, positiveText, positiveOnPress, cancelableFlag){
      Alert.alert(title, message,[{text: positiveText, onPress: positiveOnPress},], {cancelable: cancelableFlag});       
   },
   // Confirmation alert 
   confirmationAlert(message, positiveOnPress, cancelableFlag){
      confirmationAlert("Alert", message, "OK", "Cancel", positiveOnPress, () =>{console.log('Negative Pressed')}, cancelableFlag)
   },
   // confirmation alert with message
   confirmationAlert(title, message, positiveOnPress, cancelableFlag){
      confirmationAlert(title, message, "OK", "Cancel", positiveOnPress, () =>{console.log('Negative Pressed')}, cancelableFlag)
   },
   // confirmation alert with meassage and events
   confirmationAlert(title, message, positiveText, negativeText, positiveOnPress, cancelableFlag){
      confirmationAlert(title, message, positiveText, negativeText, positiveOnPress, () =>{console.log('Negative Pressed')}, cancelableFlag)
   },
   // Confirmation Alert with handling positive and negative events
   confirmationAlert(title, message, positiveText, negativeText, positiveOnPress, negativeOnPress, cancelableFlag){
      Alert.alert(
         title,
         message,
         [
           {text: negativeText, onPress: negativeOnPress, style: 'cancel',},
           {text: positiveText, onPress: positiveOnPress, style : 'default'},
         ],
         {cancelable: cancelableFlag},
       ); 
   },
   // Confirmation alert with option button
   confirmationAlertwithOptionButton(title, message, positiveText, negativeText, skipText, positiveOnPress, negativeOnPress, skipOnPress, cancelableFlag ){
      Alert.alert(
         title,
         message,
         [
           {text: skipText, onPress: skipOnPress, style: 'destructive',},
           {text: negativeText, onPress: negativeOnPress, style : 'cancel'},
           {text: positiveText, onPress: positiveOnPress, style : 'default'},
         ],
         {cancelable: cancelableFlag},
       ); 
   }

}    
export default OreAlert;