// IOP code for Button Control CNT_MC_937
import React, { Component } from 'react';
import { TouchableOpacity, Text, Button } from 'react-native';
import { StyleSheet } from 'react-native';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

// Button Control Template
export default class OreButton extends Component {
  render() {
    return (
      <TouchableOpacity
        style={[(this.props.disabled === true ? {opacity: GlobalCSS.opacity.disabledOpacity} : ""),OreStyle.OreButtonStyle, this.props.style]}
        activeOpacity={this.props.activeOpacity == undefined || this.props.activeOpacity == '' ? GlobalCSS.opacity.activeOpacity : this.props.activeOpacity}
        onPress={this.props.onPress}
        onLongPress={this.props.onLongPress}
        disabled={this.props.disabled}>
        <Text style={[OreStyle.OreButtonTextStyle, this.props.textstyle]} >{this.props.title} </Text>
      </TouchableOpacity>
    );
  }
}




