// IOP Control code for HTML is CNT_MC_959
import React, { Component } from 'react';
import { View } from 'react-native'
import { WebView } from 'react-native-webview';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

// HTML Control Template
export default class OreHtml extends Component {
  webView = {
    canGoBack: false,
    ref: null,
  }

  goBack = () => {
    this.webView.ref.goBack()
  }

  goForward = () => {
    this.webView.ref.goForward()
  }

  reload = () => {
    this.webView.ref.reload()
  }
  render() {
    return (
      <View style={this.props.style}>
        <WebView
          containerStyle={OreStyle.OreHtml}
          onLoad={this.props.onLoad}
          originWhitelist={this.props.originWhitelist ? this.props.originWhitelist : GlobalCSS.htmlProps.originWhitelist}
          source={this.props.source}
          showsHorizontalScrollIndicator={this.props.showsHorizontalScrollIndicator ? this.props.showsHorizontalScrollIndicator : GlobalCSS.htmlProps.horizontalindicator}
          showsVerticalScrollIndicator={this.props.showsVerticalScrollIndicator ? this.props.showsVerticalScrollIndicator : GlobalCSS.htmlProps.verticalindicator}
          ref={(webView) => { this.webView.ref = webView; }}
          onError={this.props.onError}
          onLoadStart={this.props.onLoadStart}
          onLoadProgress={this.props.onLoadProgress} />
      </View>
    );
  }
}


