// IOP code for Image control is CNT_MC_942
import React, { Component } from 'react';
import {Image,StyleSheet, TouchableOpacity} from 'react-native';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()
// Image Control Template
export default class OreImage extends Component {
  render() {
    return (
      <TouchableOpacity activeOpacity={this.props.activeOpacity == undefined || this.props.activeOpacity == ''  ? GlobalCSS.opacity.activeOpacity : this.props.activeOpacity} disabled={this.props.disabled} onPress={this.props.onPress} onLongPress={this.props.onLongPress}  style={[OreStyle.OreImageViewStyle,this.props.style,(this.props.disabled === true ? {opacity: GlobalCSS.opacity.disabledOpacity} : "") ]} >
        <Image 
               resizeMode={this.props.resizeMode == undefined || this.props.resizeMode == '' ? GlobalCSS.imageProps.resizeMode : this.props.resizeMode} 
               blurRadius={this.props.blurRadius} 
               source={this.props.source} 
               style={[OreStyle.OreImageStyle,this.props.imageStyle]}
               onError={this.props.onError} />
      </TouchableOpacity>
     );
  }
}

