/*This is an Example of PopuUp Dialog Example in React Native*/
import React, { Component } from 'react';
import { Button, View, Text, StyleSheet,TouchableOpacity } from 'react-native';
import Dialog,{DialogTitle,DialogContent,DialogFooter,DialogButton} from 'react-native-popup-dialog';
import OreStyle from '../OreStyle';
export default class OrePopup extends Component {
render() {
  return(
<View style={{ flex: 1 }}>
 <Dialog containerStyle={this.props.dialogcontainerStyle}
     onDismiss={() => {
       this.setState({ defaultAnimationDialog: false });
     }}
     width={this.props.dialogwidth}
     visible={this.props.visible}
     rounded
     actionsBordered
     dialogTitle={
<DialogTitle style={this.props.dialogtitlestyle}
     title={this.props.title}
     hasTitleBar={false}
     align={this.props.aligndialogtitle}
/>
     }
     footer={
<DialogFooter style={this.props.dialogfooterstyle}  >
<DialogButton style={this.props.dialogfootercancelbtn}
      text={this.props.dialogfootertcanceltext}
      bordered
      onPress={this.props.cancel}
      textStyle={{color:"#4688F4",fontSize:OreStyle.smalltext}}
      
 />
<DialogButton style={this.props.dialogfootersubmitbtn}
      text={this.props.dialogfootersubmittext}
      bordered
      onPress={this.props.submit}
      textStyle={{color:"#4688F4",fontSize:OreStyle.smalltext}}
/></DialogFooter> 
      }>
<DialogContent>
      {this.props.content}
</DialogContent>
</Dialog>
</View>
    );
  }
}



 
