// IOP code for QrCode Control CNT_MC_958
import React, { Component } from 'react';
import {View, StyleSheet} from 'react-native'
import QRCode from 'react-native-qrcode-svg';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

// QR Code Control Template
export default class OreQRCodeGenerator extends Component {
render() {
return(
<View style = {OreStyle.OreQRCodeStyle,this.props.style}>
    <QRCode
        value={this.props.value}
        color = {this.props.color ? this.props.color: GlobalCSS.color.formControlFontColor}
        size ={this.props.size ? this.props.size: GlobalCSS.qrCodeProps.size}
        logo = {this.props.isLogo ? this.props.logo: undefined}
        logoSize={this.props.size ? (this.props.size * 30) / 100 :(GlobalCSS.qrCodeProps.size * 30) / 100 }
        logoBorderRadius = {this.props.isLogo ? this.props.logoBorderRadius ? this.props.logoBorderRadius: GlobalCSS.qrCodeProps.logoBorderRadius:0}
        backgroundColor = {this.props.backgroundColor ? this.props.backgroundColor: GlobalCSS.color.pageBackgroundColor}
        logoBackgroundColor = {this.props.isLogo ? this.props.logoBackgroundColor ? this.props.logoBackgroundColor: GlobalCSS.color.pageBackgroundColor:undefined}/>
</View>
);
}
}
