// IOP control code for Radio Button is CNT_MC_945
import React, { Component } from 'react';
import {View,StyleSheet,Text} from 'react-native';
import { RadioButton} from 'react-native-paper';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

// Radio Button Control Template
export default class OreRadioButton extends Component {
render() {
return(
<View style = {[(this.props.disabled === true ? {opacity: GlobalCSS.opacity.disabledOpacity} : ""),this.props.viewStyle]}>
      <RadioButton.Group 
        onValueChange={this.props.onValueChange}
        value={this.props.selected}>
            <View style = {{flexDirection : this.props.flexDirection ? this.props.flexDirection : GlobalCSS.radioButtonProps.flexDirection}}>
                {
                    this.props.dataSource?
                    this.props.dataSource.map((option,i) =>{
                        return <View key={i} style = {OreStyle.OreRadioButtonRoot}>
                                  <RadioButton value = {option[this.props.valueField]}
                                              color = {this.props.checkedColor? this.props.checkedColor : GlobalCSS.color.primaryColor}
                                              disabled = {this.props.disabled}
                                              uncheckedColor = {this.props.unCheckedColor? this.props.unCheckedColor : GlobalCSS.color.inactiveColor} />
                                      <Text style = {[OreStyle.OreRadioButtonTextstyle,this.props.textStyle]}>{option[this.props.nameField]}</Text>
                               </View>
                    })
                    :
                    [].map((option,i) =>{
                        return <View key={i} style = {OreStyle.OreRadioButtonRoot}>
                                  <RadioButton value = {option[this.props.valueField]}
                                              color = {this.props.checkedColor? this.props.checkedColor : GlobalCSS.color.primaryColor}
                                              disabled = {this.props.disabled}
                                              uncheckedColor = {this.props.unCheckedColor? this.props.unCheckedColor : GlobalCSS.color.inactiveColor} />
                                      <Text style = {[OreStyle.OreRadioButtonTextstyle,this.props.textStyle]}>{option[this.props.nameField]}</Text>
                               </View>
                    })
                }   
            </View>
      </RadioButton.Group>
      </View>
      );
    }
    }

    // Default CSS Properties for Radio Button
    const styles = StyleSheet.create({
        oreTextstyle:{
            color:OreStyle.textcolor,
            fontSize :  16   ,
            alignSelf : 'center' 
        },
        oreRootViewStyle:{
            color:OreStyle.textcolor,
            fontSize :  16    
        }
    });