// IOP control code for Switch is CNT_MC_946
import React, { Component } from 'react';
import {Switch,StyleSheet} from 'react-native';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

//Switch Control Template
export default class OreSwitch extends Component {
  render() {
    return (
     <Switch  style={[(this.props.disabled === true ? {opacity: GlobalCSS.opacity.disabledOpacity} : ""),OreStyle.OreSwitch,this.props.style]}
     thumbColor={this.props.value ? (this.props.thumbColorOn == undefined || this.props.thumbColorOn == '' ? GlobalCSS.color.primaryColor : this.props.thumbColorOn): (this.props.thumbColorOff == undefined || this.props.thumbColorOff == '' ? GlobalCSS.color.actionControlFontColor : this.props.thumbColorOff)} 
     trackColor={this.props.trackColor == undefined || this.props.trackColor == '' ? {true:GlobalCSS.color.primaryLightColor,false:GlobalCSS.color.inactiveColor} : this.props.trackColor} 
     disabled={this.props.disabled}
     onValueChange={this.props.onValueChange} 
     value={this.props.value} />
     );
  }
}