import React, { Component } from 'react';
import {View,StyleSheet,Text} from 'react-native';
import themebuilder from '../../../../themebuilder';
let OreStyle = themebuilder.getStylePath()
let GlobalCSS = themebuilder.getPropertyPath()

// Text Control Template
export default class OreText extends Component {
render() {
return(
<Text ellipsizeMode={"tail"} numberOfLines={this.props.numberOfLines} onPress={this.props.onPress} onLongPress={this.props.onLongPress} selectable={this.props.selectable == undefined || this.props.selectable == '' ? GlobalCSS.textProps.selectable : this.props.selectable} style={[OreStyle.OreText,(this.props.disabled === true ? {opacity:GlobalCSS.opacity.disabledOpacity} : ""),this.props.style]} disabled={this.props.disabled}>{this.props.title}</Text>
);
}
}

